import React, { useState } from "react";
import "./Overlay.css";
import TextField from "./TextField";
import Overlay1 from "./Overlay1";
import SubmitButton from "./SubmitButton";
// import { BiChevronDown, BiChevronUp } from 'react-icons/bi';

const Overlay = ({ onClose }) => {
  const [isAdvancedConfig, setAdvancedConfig] = useState(false);
  //const [isOverlayOpen, setOverlayOpen] = useState(false);
 
 // const [arrow, setArrow] = useState(true);
  //const [isPopupOpen, setPopupOpen] = useState(false);

  // const handleClosePopup = () => {
  //   setPopupOpen(false);
  // };

  //   const handleInputChange = (e) => {
  //     setInputValue(e.target.value);
  //   };

  //   const handleConfirm = () => {
  //     onConfirm(inputValue);
  //     onClose();
  //   };
  const handleOpenAdvancedConfig = () => {
    setAdvancedConfig(!isAdvancedConfig);
  }; 

  function handleDummyApiCall(){
    console.log("API called")
  }
 

  return (
    <div className="overlay">
      <div className="overlay-content">
       <div className="first-line">
         <h4>Create Ordering Service</h4>
         <i class="bi bi-x-square" style={{marginRight:'10px', padding:'10px'}} ></i>
       </div>
      

        <div className="first-row">
          <TextField label="Organization Name" tag="*"   holder ="Organization Name 1"/>
          <TextField label="No.of Ordering Nodes" tag="*" holder ="Organization Name 1"/>
        </div>
        <div className="second-row">
          <TextField label="Channel Name" tag="(Optional)" holder ="Organization Name 1"/>
          <TextField label="Domain" tag="*" holder ="Organization Name 1"/>
        </div>

        <h4>
          Advanced Configurations
          <i
            onClick={handleOpenAdvancedConfig}
          >
             <i className={`bi bi-chevron-${isAdvancedConfig ? 'up' : 'down'}`}></i>
          </i>
        </h4>
        {isAdvancedConfig && (
          <div className="advanced-config">
            <Overlay1 />
          </div>
        )}
      </div>
      <SubmitButton onClick={handleDummyApiCall}/>
    </div>
  );
};
export default Overlay;
