import React from 'react'
import './TextField.css'

function TextField({label,tag}) {
    const tagStyle = {
        color: tag === '*' ? 'red' : 'inherit'
};
  return (
    
    <div className='text-field'>
      <label>{label} <span style={tagStyle}>{tag}</span></label>
        <br/>
        <input type="text" placeholder="Organization Name 1"/>
      </div>   
  )
}

export default TextField
