import React, { useState } from "react";
import "./Overlay.css";
import TextField from "./TextField";
import Overlay1 from "./Overlay1";
const Overlay = ({ onClose, onConfirm }) => {
    const [isAdvanceVisible, setIsAdvanceVisible] = useState(false);
  //   const [inputValue, setInputValue] = useState('');

  //   const handleInputChange = (e) => {
  //     setInputValue(e.target.value);
  //   };

  //   const handleConfirm = () => {
  //     onConfirm(inputValue);
  //     onClose();
  //   };

  const handleOpenOverlay1 = () => {
    setIsAdvanceVisible(true);
  };

  const handleCloseOverlay1 = () => {
    setIsAdvanceVisible(false);
  };

  
  const handleConfirmOverlay1 = (text) => {
   
    console.log("Confirmed Text:", text);
  };
  function handleAdvanceConfig(){

  }

  return (
    <div className="overlay">
      <div className="overlay-content">
        <h4>Create Ordering Service</h4>
        <div className="first-row">
          <TextField label="Organization Name" tag="*" />
          <TextField label="No.of Ordering Nodes" tag="*" />
        </div>
        <div className="second-row">
          <TextField label="Channel Name" tag="(Optional)" />
          <TextField label="Domain" tag="*" />
        </div>
        <div className="advanced-config">
        <h4>Advanced Configurations <i className="bi bi-chevron-down" onClick={handleAdvanceConfig}></i></h4>
        </div>
        {isAdvanceVisible && (
        <Overlay1
          onClose={handleCloseOverlay1}
          onConfirm={handleConfirmOverlay1}
        />
      )}
        
        <div className="submit-buttons">
          <button className="cancel">Cancel</button>
          <button className="submit">Submit</button>
        </div>
      </div>
    </div>
  );
};
export default Overlay;
