import React, { useState } from "react";
import "./Overlay.css";
import TextField from "./TextField";
import Overlay1 from "./Overlay1";
const Overlay = ({ onClose }) => {
    const [isAdvancedConfig, setAdvancedConfig] = useState(false);

  //   const handleInputChange = (e) => {
  //     setInputValue(e.target.value);
  //   };

  //   const handleConfirm = () => {
  //     onConfirm(inputValue);
  //     onClose();
  //   };
  const handleOpenAdvancedConfig = () => {
    setAdvancedConfig(true);
  };

  return (
    <div className="overlay">
      <div className="overlay-content">
       
        <h4>{isAdvancedConfig ? 'Advanced Configurations' : 'Create Ordering Service'}</h4>
         {isAdvancedConfig ? (
            <Overlay1 onClose = {onClose}/>
         ) : (
            <>
            <div className="first-row">
          <TextField label="Organization Name" tag="*" />
          <TextField label="No.of Ordering Nodes" tag="*" />
        </div>
        <div className="second-row">
          <TextField label="Channel Name" tag="(Optional)" />
          <TextField label="Domain" tag="*" />
        </div> </>
         )}
         <div className="advanced-config">
        <h4>Advanced Configurations <i className="bi bi-chevron-down" onClick={handleOpenAdvancedConfig}></i></h4>
        </div>
        {/* <div className="first-row">
          <TextField label="Organization Name" tag="*" />
          <TextField label="No.of Ordering Nodes" tag="*" />
        </div>
        <div className="second-row">
          <TextField label="Channel Name" tag="(Optional)" />
          <TextField label="Domain" tag="*" />
        </div>
        <div className="advanced-config">
        <h4>Advanced Configurations <i className="bi bi-chevron-down"></i></h4>
        </div>
         */}

        <div className="submit-buttons">
          <button className="cancel">Cancel</button>
          <button className="submit">Submit</button>
        </div>
      </div>
    </div>
  );
};
export default Overlay;
