import { useState } from "react";
import "./App.css";
import Overlay from "./Components/Overlay";
import Overlay1 from "./Components/Overlay1";


function App() {
  const [isPopupOpen, setPopupOpen] = useState(false);
  const [isAdvancedConfig, setAdvancedConfig] = useState(false);

  const handleOpenPopup = () => {
    setPopupOpen(true);
    setAdvancedConfig(false);
  };

  const handleClosePopup = () => {
    setPopupOpen(false);
  };
  const handleOpenAdvancedConfig = () => {
    setAdvancedConfig(true);
  };
  // const handleConfirmOverlay = (text) => {

  //   console.log("Confirmed Text:", text);
  // };
  return (
    <div>
      <button className="button" onClick={handleOpenPopup}>
        <i className="bi bi-plus"></i> Add Ordering Service
      </button>
      . {isPopupOpen && (
        <div className="popup-overlay">
          <div className="popup-content">
            <h2>{isAdvancedConfig ? <Overlay1/> : <Overlay/>}</h2>

            {isAdvancedConfig && (
              <div>
              <div className="advanced-config">
             <h4>Advanced Configurations <i className="bi bi-chevron-down" onClick={handleAdvanceConfig}></i></h4>
        </div>
                <p>Advanced Options</p>
              </div>
            )}

            <button onClick={handleClosePopup}>Cancel</button>
            <button onClick={handleOpenAdvancedConfig}>Advanced Configurations</button>

          </div>
        </div>
      )}
       {/* {isPopupOpen  && (
        <Overlay
          onClose={handleCloseOverlay}
          onConfirm={handleConfirmOverlay}
        />
      )} */}
    </div>
  );
}

export default App;
